import Foundation
import VPNManager
import LocationService
import TimeService

// MARK: - NetworkError
enum NetworkError: Error {
    case invalidURL
    case noData
}

protocol HomeBusinessLogic {
    func connectToVPN()
    func disconnectFromVPN()
    func viewDidAppear()
}

protocol HomeDataStore {
    //var name: String { get set }
}

final class HomeInteractor: HomeBusinessLogic, HomeDataStore {
    private struct Settings {
        static let tickInterval: TimeInterval = 1
    }
    
    weak var presenter: HomePresentationLogic?
    
    private let vpnManager: VPNManagerProtocol
    private let locationService: LocationService
    private let timeService: TimeService
    
    private var connectionTime: TimeInterval = 0 {
        didSet { presenter?.connectionTime(connectionTime) }
    }
    
    //MARK: - init(_:)
    init(
        vpnManager: VPNManagerProtocol,
        locationService: LocationService,
        timeService: TimeService = TimeManagerImpl.shared
    ) {
        self.vpnManager = vpnManager
        self.locationService = locationService
        self.timeService = timeService
    }
    
    //MARK: - Public methods
    func viewDidAppear() {
        guard let selectedService = locationService.currentLocation() else {
            presenter?.serverUnavailable()
            return
        }
        presenter?.currentServer(selectedService)
    }
    
    func connectToVPN() {
        presenter?.connectionState(.loading)
        vpnManager.connectToVPN(
            .init(
                userName: "",
                serverAddress: ""
            ),
            completion: handler()
        )
    }
    
    func disconnectFromVPN() {
        vpnManager.disconnectFromVPN()
        timeService.stop()
        presenter?.connectionState(.disconnected)
    }
    
}

//MARK: - private methods
private extension HomeInteractor {
    func startTimer() {
        timeService.start(interval: Settings.tickInterval) {
            self.connectionTime += 1
        }
    }
    
    func handler() -> (Error?) -> Void {
        { [weak self] error in
            guard let self else { return }
            switch error {
            case .some(let error):
                presenter?.connectionState(.error(error))
            case .none:
                presenter?.connectionState(.connected)
                startTimer()
            }
        }
    }
}
